INSERT INTO `recipes` (`title`, `slug`, `description`, `instructions`, `ingredients`, `user_id`, `recipe_category_id`, `prep_time`, `cook_time`, `total_time`, `servings`, `difficulty`, `tags`, `chef_notes`, `rating`, `rating_count`, `views`, `is_featured`, `is_published`, `published_at`, `created_at`, `updated_at`) VALUES
('Ev Yapımı Mantarli Spagetti', 'ev-yapimi-mantarli-spagetti', 'Kremsi mantar soslu nefis spagetti tarifi. Evde kolayca yapabileceğiniz restaurant lezzeti.', '<h3>Hazırlık Aşaması</h3><p>Makarnayı kaynar tuzlu suda al dente olana kadar pişirin.</p><h3>Sos Hazırlama</h3><p>Tavada tereyağını eritin, mantarları ekleyin ve kavurun.</p><p>Krema ve baharatları ekleyip karıştırın.</p><h3>Sunum</h3><p>Makarna ile sosu karıştırıp sıcak servis yapın.</p>', JSON_ARRAY('400g spagetti makarna', '300g taze mantar', '200ml krema', '2 diş sarımsak', '1 soğan', '50g tereyağı', 'Tuz, karabiber', 'Rendelenmiş parmesan peyniri'), 2, 1, 15, 25, 40, 4, 'orta', JSON_ARRAY('makarna', 'mantar', 'kremali', 'italyan'), 'Mantarları iyi kavurmak lezzeti artırır.', 4.50, 23, 1250, 1, 1, DATE_SUB(NOW(), INTERVAL 5 DAY), NOW(), NOW()),

('Çikolatalı Sufle', 'cikolatali-sufle', 'İçi akıkan, dışı çıtır nefis çikolatalı sufle. Misafirlerinizi etkileyecek desert.', '<h3>Sufle Hazırlığı</h3><p>Çikolata ve tereyağını benmari usulü eritin.</p><p>Yumurta sarılarını şekerle çırpın.</p><h3>Pişirme</h3><p>Karışımı kaliplara doldurun.</p><p>220°C fırında 12 dakika pişirin.</p>', JSON_ARRAY('200g bitter çikolata', '100g tereyağı', '4 yumurta', '80g toz şeker', '2 yemek kaşığı un', 'Vanilin', 'Tereyağı (kalip için)'), 2, 2, 20, 12, 32, 6, 'zor', JSON_ARRAY('çikolata', 'sufle', 'desert', 'fırın'), 'Sufleyi hemen servis edin, bekletmeyin.', 4.80, 15, 890, 1, 1, DATE_SUB(NOW(), INTERVAL 8 DAY), NOW(), NOW()),

('Mercimek Çorbası', 'mercimek-corbasi', 'Geleneksel Türk mutfağının vazgeçilmez lezzeti mercimek çorbası.', '<h3>Mercimek Hazırlığı</h3><p>Kırmızı mercimeği yıkayıp süzdürün.</p><h3>Pişirme</h3><p>Sebzelerle birlikte haşlayın.</p><p>Blender\'dan geçirip kreması haline getirin.</p>', JSON_ARRAY('1 su bardağı kırmızı mercimek', '1 orta boy soğan', '1 havuç', '2 yemek kaşığı zeytinyağı', '1 tatlı kaşığı domates salçası', 'Tuz, karabiber', 'Limon'), 2, 3, 10, 30, 40, 4, 'kolay', JSON_ARRAY('mercimek', 'çorba', 'geleneksel', 'sağlıklı'), 'Servis sırasında limon sıkmayı unutmayın.', 4.30, 31, 2180, 0, 1, DATE_SUB(NOW(), INTERVAL 12 DAY), NOW(), NOW()),

('Çoban Salatası', 'coban-salatasi', 'Taze sebzelerle hazırlanan geleneksel çoban salatası. Hafif ve lezzetli.', '<h3>Sebze Hazırlığı</h3><p>Tüm sebzeleri küçük küp şeklinde doğrayın.</p><h3>Karıştırma</h3><p>Büyük bir kasede tüm malzemeleri karıştırın.</p><p>Sos malzemelerini ekleyip karıştırın.</p>', JSON_ARRAY('3 domates', '2 salatalık', '1 yesil biber', '1 kırmızı soğan', '100g beyaz peynir', 'Maydanoz', '3 yemek kaşığı zeytinyağı', '1 yemek kaşığı limon suyu', 'Tuz'), 2, 4, 15, 0, 15, 4, 'kolay', JSON_ARRAY('salata', 'taze', 'geleneksel', 'sağlıklı'), 'Sebzeleri servis öncesinde doğrayın.', 4.10, 18, 743, 0, 1, DATE_SUB(NOW(), INTERVAL 15 DAY), NOW(), NOW()),

('Ev Yapımı Limonata', 'ev-yapimi-limonata', 'Serinleten, taze limonlardan hazırlanan ev yapımı limonata.', '<h3>Şerbet Hazırlığı</h3><p>Şeker ve suyu kaynatıp şerbet yapın.</p><h3>Karıştırma</h3><p>Limon suyunu soğuk şerbete karıştırın.</p><p>Buzla servis yapın.</p>', JSON_ARRAY('6 taze limon', '1 su bardağı şeker', '4 su bardağı su', 'Buz', 'Nane yaprakları (süsleme)'), 2, 5, 10, 5, 15, 4, 'kolay', JSON_ARRAY('limonata', 'serinletici', 'yaz', 'ev yapımı'), 'Limonları sıkmadan önce oda sıcaklığında bekletin.', 4.00, 12, 560, 0, 1, DATE_SUB(NOW(), INTERVAL 18 DAY), NOW(), NOW());

